import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as a,e as p}from"./app.639560eb.js";const r={},d=p('<h1 id="_13-redis\u6301\u4E45\u5316\u4E4Baof" tabindex="-1"><a class="header-anchor" href="#_13-redis\u6301\u4E45\u5316\u4E4Baof" aria-hidden="true">#</a> 13.Redis\u6301\u4E45\u5316\u4E4BAOF</h1><h2 id="_13-1-aof-append-only-file" tabindex="-1"><a class="header-anchor" href="#_13-1-aof-append-only-file" aria-hidden="true">#</a> 13.1 AOF\uFF08Append Only File\uFF09</h2><h3 id="_13-1-1-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#_13-1-1-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> 13.1.1 \u662F\u4EC0\u4E48</h3><p>\u4EE5\u65E5\u5FD7\u7684\u5F62\u5F0F\u6765\u8BB0\u5F55\u6BCF\u4E2A\u5199\u64CD\u4F5C\uFF08\u589E\u91CF\u4FDD\u5B58\uFF09\uFF0C\u5C06Redis\u6267\u884C\u8FC7\u7684\u6240\u6709\u5199\u6307\u4EE4\u8BB0\u5F55\u4E0B\u6765(\u8BFB\u64CD\u4F5C\u4E0D\u8BB0\u5F55)\uFF0C \u53EA\u8BB8\u8FFD\u52A0\u6587\u4EF6\u4F46\u4E0D\u53EF\u4EE5\u6539\u5199\u6587\u4EF6\uFF0Credis\u542F\u52A8\u4E4B\u521D\u4F1A\u8BFB\u53D6\u8BE5\u6587\u4EF6\u91CD\u65B0\u6784\u5EFA\u6570\u636E\uFF0C\u6362\u8A00\u4E4B\uFF0Credis \u91CD\u542F\u7684\u8BDD\u5C31\u6839\u636E\u65E5\u5FD7\u6587\u4EF6\u7684\u5185\u5BB9\u5C06\u5199\u6307\u4EE4\u4ECE\u524D\u5230\u540E\u6267\u884C\u4E00\u6B21\u4EE5\u5B8C\u6210\u6570\u636E\u7684\u6062\u590D\u5DE5\u4F5C</p><h3 id="_13-1-2-aof\u6301\u4E45\u5316\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#_13-1-2-aof\u6301\u4E45\u5316\u6D41\u7A0B" aria-hidden="true">#</a> 13.1.2 AOF\u6301\u4E45\u5316\u6D41\u7A0B</h3><p>\uFF081\uFF09\u5BA2\u6237\u7AEF\u7684\u8BF7\u6C42\u5199\u547D\u4EE4\u4F1A\u88ABappend\u8FFD\u52A0\u5230AOF\u7F13\u51B2\u533A\u5185\uFF1B</p><p>\uFF082\uFF09AOF\u7F13\u51B2\u533A\u6839\u636EAOF\u6301\u4E45\u5316\u7B56\u7565[always,everysec,no]\u5C06\u64CD\u4F5Csync\u540C\u6B65\u5230\u78C1\u76D8\u7684AOF\u6587\u4EF6\u4E2D\uFF1B</p><p>\uFF083\uFF09AOF\u6587\u4EF6\u5927\u5C0F\u8D85\u8FC7\u91CD\u5199\u7B56\u7565\u6216\u624B\u52A8\u91CD\u5199\u65F6\uFF0C\u4F1A\u5BF9AOF\u6587\u4EF6rewrite\u91CD\u5199\uFF0C\u538B\u7F29AOF\u6587\u4EF6\u5BB9\u91CF\uFF1B</p><p>\uFF084\uFF09Redis\u670D\u52A1\u91CD\u542F\u65F6\uFF0C\u4F1A\u91CD\u65B0load\u52A0\u8F7DAOF\u6587\u4EF6\u4E2D\u7684\u5199\u64CD\u4F5C\u8FBE\u5230\u6570\u636E\u6062\u590D\u7684\u76EE\u7684\uFF1B</p><p><img src="https://img-1305804786.cos.ap-beijing.myqcloud.com//picgo202210221449478.jpg" alt="img" loading="lazy"></p><h3 id="_13-1-3-aof\u9ED8\u8BA4\u4E0D\u5F00\u542F" tabindex="-1"><a class="header-anchor" href="#_13-1-3-aof\u9ED8\u8BA4\u4E0D\u5F00\u542F" aria-hidden="true">#</a> 13.1.3 AOF\u9ED8\u8BA4\u4E0D\u5F00\u542F</h3><p>\u53EF\u4EE5\u5728redis.conf\u4E2D\u914D\u7F6E\u6587\u4EF6\u540D\u79F0\uFF0C\u9ED8\u8BA4\u4E3A appendonly.aof</p><p>AOF\u6587\u4EF6\u7684\u4FDD\u5B58\u8DEF\u5F84\uFF0C\u540CRDB\u7684\u8DEF\u5F84\u4E00\u81F4\u3002</p><h3 id="_13-1-4-aof\u548Crdb\u540C\u65F6\u5F00\u542F-redis\u542C\u8C01\u7684" tabindex="-1"><a class="header-anchor" href="#_13-1-4-aof\u548Crdb\u540C\u65F6\u5F00\u542F-redis\u542C\u8C01\u7684" aria-hidden="true">#</a> 13.1.4 AOF\u548CRDB\u540C\u65F6\u5F00\u542F\uFF0Credis\u542C\u8C01\u7684\uFF1F</h3><p>AOF\u548CRDB\u540C\u65F6\u5F00\u542F\uFF0C\u7CFB\u7EDF\u9ED8\u8BA4\u53D6AOF\u7684\u6570\u636E\uFF08\u6570\u636E\u4E0D\u4F1A\u5B58\u5728\u4E22\u5931\uFF09</p><h3 id="_13-1-5-aof\u542F\u52A8-\u4FEE\u590D-\u6062\u590D" tabindex="-1"><a class="header-anchor" href="#_13-1-5-aof\u542F\u52A8-\u4FEE\u590D-\u6062\u590D" aria-hidden="true">#</a> 13.1.5 AOF\u542F\u52A8/\u4FEE\u590D/\u6062\u590D</h3><ul><li><p>AOF\u7684\u5907\u4EFD\u673A\u5236\u548C\u6027\u80FD\u867D\u7136\u548CRDB\u4E0D\u540C, \u4F46\u662F\u5907\u4EFD\u548C\u6062\u590D\u7684\u64CD\u4F5C\u540CRDB\u4E00\u6837\uFF0C\u90FD\u662F\u62F7\u8D1D\u5907\u4EFD\u6587\u4EF6\uFF0C\u9700\u8981\u6062\u590D\u65F6\u518D\u62F7\u8D1D\u5230Redis\u5DE5\u4F5C\u76EE\u5F55\u4E0B\uFF0C\u542F\u52A8\u7CFB\u7EDF\u5373\u52A0\u8F7D\u3002</p></li><li><p>\u6B63\u5E38\u6062\u590D</p><ul><li><p>\u4FEE\u6539\u9ED8\u8BA4\u7684appendonly no\uFF0C\u6539\u4E3Ayes</p></li><li><p>\u5C06\u6709\u6570\u636E\u7684aof\u6587\u4EF6\u590D\u5236\u4E00\u4EFD\u4FDD\u5B58\u5230\u5BF9\u5E94\u76EE\u5F55(\u67E5\u770B\u76EE\u5F55\uFF1Aconfig get dir)</p></li><li><p>\u6062\u590D\uFF1A\u91CD\u542Fredis\u7136\u540E\u91CD\u65B0\u52A0\u8F7D</p></li></ul></li><li><p>\u5F02\u5E38\u6062\u590D</p><ul><li><p>\u4FEE\u6539\u9ED8\u8BA4\u7684appendonly no\uFF0C\u6539\u4E3Ayes</p></li><li><p>\u5982\u9047\u5230AOF\u6587\u4EF6\u635F\u574F\uFF0C\u901A\u8FC7/usr/local/bin/redis-check-aof--fix appendonly.aof\u8FDB\u884C\u6062\u590D</p></li><li><p>\u5907\u4EFD\u88AB\u5199\u574F\u7684AOF\u6587\u4EF6</p></li><li><p>\u6062\u590D\uFF1A\u91CD\u542Fredis\uFF0C\u7136\u540E\u91CD\u65B0\u52A0\u8F7D</p></li></ul></li></ul><h3 id="_13-1-6-aof\u540C\u6B65\u9891\u7387\u8BBE\u7F6E" tabindex="-1"><a class="header-anchor" href="#_13-1-6-aof\u540C\u6B65\u9891\u7387\u8BBE\u7F6E" aria-hidden="true">#</a> 13.1.6 AOF\u540C\u6B65\u9891\u7387\u8BBE\u7F6E</h3><p>appendfsync always</p><p>\u59CB\u7EC8\u540C\u6B65\uFF0C\u6BCF\u6B21Redis\u7684\u5199\u5165\u90FD\u4F1A\u7ACB\u523B\u8BB0\u5165\u65E5\u5FD7\uFF1B\u6027\u80FD\u8F83\u5DEE\u4F46\u6570\u636E\u5B8C\u6574\u6027\u6BD4\u8F83\u597D</p><p>appendfsync everysec</p><p>\u6BCF\u79D2\u540C\u6B65\uFF0C\u6BCF\u79D2\u8BB0\u5165\u65E5\u5FD7\u4E00\u6B21\uFF0C\u5982\u679C\u5B95\u673A\uFF0C\u672C\u79D2\u7684\u6570\u636E\u53EF\u80FD\u4E22\u5931\u3002</p><p>appendfsync no</p><p>redis\u4E0D\u4E3B\u52A8\u8FDB\u884C\u540C\u6B65\uFF0C\u628A\u540C\u6B65\u65F6\u673A\u4EA4\u7ED9\u64CD\u4F5C\u7CFB\u7EDF\u3002</p><h3 id="_13-1-7-rewrite\u538B\u7F29" tabindex="-1"><a class="header-anchor" href="#_13-1-7-rewrite\u538B\u7F29" aria-hidden="true">#</a> 13.1.7 Rewrite\u538B\u7F29</h3><p>1\u3001\u662F\u4EC0\u4E48\uFF1A</p><p>AOF\u91C7\u7528\u6587\u4EF6\u8FFD\u52A0\u65B9\u5F0F\uFF0C\u6587\u4EF6\u4F1A\u8D8A\u6765\u8D8A\u5927\u4E3A\u907F\u514D\u51FA\u73B0\u6B64\u79CD\u60C5\u51B5\uFF0C\u65B0\u589E\u4E86\u91CD\u5199\u673A\u5236, \u5F53AOF\u6587\u4EF6\u7684\u5927\u5C0F\u8D85\u8FC7\u6240\u8BBE\u5B9A\u7684\u9608\u503C\u65F6\uFF0CRedis\u5C31\u4F1A\u542F\u52A8AOF\u6587\u4EF6\u7684\u5185\u5BB9\u538B\u7F29\uFF0C \u53EA\u4FDD\u7559\u53EF\u4EE5\u6062\u590D\u6570\u636E\u7684\u6700\u5C0F\u6307\u4EE4\u96C6.\u53EF\u4EE5\u4F7F\u7528\u547D\u4EE4bgrewriteaof</p><p>2\u3001\u91CD\u5199\u539F\u7406\uFF0C\u5982\u4F55\u5B9E\u73B0\u91CD\u5199</p><p>AOF\u6587\u4EF6\u6301\u7EED\u589E\u957F\u800C\u8FC7\u5927\u65F6\uFF0C\u4F1Afork\u51FA\u4E00\u6761\u65B0\u8FDB\u7A0B\u6765\u5C06\u6587\u4EF6\u91CD\u5199(\u4E5F\u662F\u5148\u5199\u4E34\u65F6\u6587\u4EF6\u6700\u540E\u518Drename)\uFF0Credis4.0\u7248\u672C\u540E\u7684\u91CD\u5199\uFF0C\u662F\u6307\u4E0A\u5C31\u662F\u628Ardb \u7684\u5FEB\u7167\uFF0C\u4EE5\u4E8C\u7EA7\u5236\u7684\u5F62\u5F0F\u9644\u5728\u65B0\u7684aof\u5934\u90E8\uFF0C\u4F5C\u4E3A\u5DF2\u6709\u7684\u5386\u53F2\u6570\u636E\uFF0C\u66FF\u6362\u6389\u539F\u6765\u7684\u6D41\u6C34\u8D26\u64CD\u4F5C\u3002</p><p>no-appendfsync-on-rewrite\uFF1A</p><p>\u5982\u679C no-appendfsync-on-rewrite=yes ,\u4E0D\u5199\u5165aof\u6587\u4EF6\u53EA\u5199\u5165\u7F13\u5B58\uFF0C\u7528\u6237\u8BF7\u6C42\u4E0D\u4F1A\u963B\u585E\uFF0C\u4F46\u662F\u5728\u8FD9\u6BB5\u65F6\u95F4\u5982\u679C\u5B95\u673A\u4F1A\u4E22\u5931\u8FD9\u6BB5\u65F6\u95F4\u7684\u7F13\u5B58\u6570\u636E\u3002\uFF08\u964D\u4F4E\u6570\u636E\u5B89\u5168\u6027\uFF0C\u63D0\u9AD8\u6027\u80FD\uFF09</p><p>\u5982\u679C no-appendfsync-on-rewrite=no, \u8FD8\u662F\u4F1A\u628A\u6570\u636E\u5F80\u78C1\u76D8\u91CC\u5237\uFF0C\u4F46\u662F\u9047\u5230\u91CD\u5199\u64CD\u4F5C\uFF0C\u53EF\u80FD\u4F1A\u53D1\u751F\u963B\u585E\u3002\uFF08\u6570\u636E\u5B89\u5168\uFF0C\u4F46\u662F\u6027\u80FD\u964D\u4F4E\uFF09</p><p>\u89E6\u53D1\u673A\u5236\uFF0C\u4F55\u65F6\u91CD\u5199</p><p>Redis\u4F1A\u8BB0\u5F55\u4E0A\u6B21\u91CD\u5199\u65F6\u7684AOF\u5927\u5C0F\uFF0C\u9ED8\u8BA4\u914D\u7F6E\u662F\u5F53AOF\u6587\u4EF6\u5927\u5C0F\u662F\u4E0A\u6B21rewrite\u540E\u5927\u5C0F\u7684\u4E00\u500D\u4E14\u6587\u4EF6\u5927\u4E8E64M\u65F6\u89E6\u53D1</p><p>\u91CD\u5199\u867D\u7136\u53EF\u4EE5\u8282\u7EA6\u5927\u91CF\u78C1\u76D8\u7A7A\u95F4\uFF0C\u51CF\u5C11\u6062\u590D\u65F6\u95F4\u3002\u4F46\u662F\u6BCF\u6B21\u91CD\u5199\u8FD8\u662F\u6709\u4E00\u5B9A\u7684\u8D1F\u62C5\u7684\uFF0C\u56E0\u6B64\u8BBE\u5B9ARedis\u8981\u6EE1\u8DB3\u4E00\u5B9A\u6761\u4EF6\u624D\u4F1A\u8FDB\u884C\u91CD\u5199\u3002</p><p>auto-aof-rewrite-percentage\uFF1A\u8BBE\u7F6E\u91CD\u5199\u7684\u57FA\u51C6\u503C\uFF0C\u6587\u4EF6\u8FBE\u5230100%\u65F6\u5F00\u59CB\u91CD\u5199\uFF08\u6587\u4EF6\u662F\u539F\u6765\u91CD\u5199\u540E\u6587\u4EF6\u76842\u500D\u65F6\u89E6\u53D1\uFF09</p><p>auto-aof-rewrite-min-size\uFF1A\u8BBE\u7F6E\u91CD\u5199\u7684\u57FA\u51C6\u503C\uFF0C\u6700\u5C0F\u6587\u4EF664MB\u3002\u8FBE\u5230\u8FD9\u4E2A\u503C\u5F00\u59CB\u91CD\u5199\u3002</p><p>\u4F8B\u5982\uFF1A\u6587\u4EF6\u8FBE\u523070MB\u5F00\u59CB\u91CD\u5199\uFF0C\u964D\u523050MB\uFF0C\u4E0B\u6B21\u4EC0\u4E48\u65F6\u5019\u5F00\u59CB\u91CD\u5199\uFF1F100MB</p><p>\u7CFB\u7EDF\u8F7D\u5165\u65F6\u6216\u8005\u4E0A\u6B21\u91CD\u5199\u5B8C\u6BD5\u65F6\uFF0CRedis\u4F1A\u8BB0\u5F55\u6B64\u65F6AOF\u5927\u5C0F\uFF0C\u8BBE\u4E3Abase_size,</p><p>\u5982\u679CRedis\u7684AOF\u5F53\u524D\u5927\u5C0F&gt;= base_size +base_size*100% (\u9ED8\u8BA4)\u4E14\u5F53\u524D\u5927\u5C0F&gt;=64mb(\u9ED8\u8BA4)\u7684\u60C5\u51B5\u4E0B\uFF0CRedis\u4F1A\u5BF9AOF\u8FDB\u884C\u91CD\u5199\u3002</p><p>3\u3001\u91CD\u5199\u6D41\u7A0B</p><p>\uFF081\uFF09bgrewriteaof\u89E6\u53D1\u91CD\u5199\uFF0C\u5224\u65AD\u662F\u5426\u5F53\u524D\u6709bgsave\u6216bgrewriteaof\u5728\u8FD0\u884C\uFF0C\u5982\u679C\u6709\uFF0C\u5219\u7B49\u5F85\u8BE5\u547D\u4EE4\u7ED3\u675F\u540E\u518D\u7EE7\u7EED\u6267\u884C\u3002</p><p>\uFF082\uFF09\u4E3B\u8FDB\u7A0Bfork\u51FA\u5B50\u8FDB\u7A0B\u6267\u884C\u91CD\u5199\u64CD\u4F5C\uFF0C\u4FDD\u8BC1\u4E3B\u8FDB\u7A0B\u4E0D\u4F1A\u963B\u585E\u3002</p><p>\uFF083\uFF09\u5B50\u8FDB\u7A0B\u904D\u5386redis\u5185\u5B58\u4E2D\u6570\u636E\u5230\u4E34\u65F6\u6587\u4EF6\uFF0C\u5BA2\u6237\u7AEF\u7684\u5199\u8BF7\u6C42\u540C\u65F6\u5199\u5165aof_buf\u7F13\u51B2\u533A\u548Caof_rewrite_buf\u91CD\u5199\u7F13\u51B2\u533A\u4FDD\u8BC1\u539FAOF\u6587\u4EF6\u5B8C\u6574\u4EE5\u53CA\u65B0AOF\u6587\u4EF6\u751F\u6210\u671F\u95F4\u7684\u65B0\u7684\u6570\u636E\u4FEE\u6539\u52A8\u4F5C\u4E0D\u4F1A\u4E22\u5931\u3002</p><p>\uFF084\uFF091).\u5B50\u8FDB\u7A0B\u5199\u5B8C\u65B0\u7684AOF\u6587\u4EF6\u540E\uFF0C\u5411\u4E3B\u8FDB\u7A0B\u53D1\u4FE1\u53F7\uFF0C\u7236\u8FDB\u7A0B\u66F4\u65B0\u7EDF\u8BA1\u4FE1\u606F\u30022).\u4E3B\u8FDB\u7A0B\u628Aaof_rewrite_buf\u4E2D\u7684\u6570\u636E\u5199\u5165\u5230\u65B0\u7684AOF\u6587\u4EF6\u3002</p><p>\uFF085\uFF09\u4F7F\u7528\u65B0\u7684AOF\u6587\u4EF6\u8986\u76D6\u65E7\u7684AOF\u6587\u4EF6\uFF0C\u5B8C\u6210AOF\u91CD\u5199\u3002</p><p><img src="https://img-1305804786.cos.ap-beijing.myqcloud.com//picgo202210221449573.jpg" alt="img" loading="lazy"></p><h3 id="_13-1-8-\u4F18\u52BF" tabindex="-1"><a class="header-anchor" href="#_13-1-8-\u4F18\u52BF" aria-hidden="true">#</a> 13.1.8 \u4F18\u52BF</h3><p><img src="https://img-1305804786.cos.ap-beijing.myqcloud.com//picgo202210221449681.png" alt="img" loading="lazy"></p><ul><li><p>\u5907\u4EFD\u673A\u5236\u66F4\u7A33\u5065\uFF0C\u4E22\u5931\u6570\u636E\u6982\u7387\u66F4\u4F4E\u3002</p></li><li><p>\u53EF\u8BFB\u7684\u65E5\u5FD7\u6587\u672C\uFF0C\u901A\u8FC7\u64CD\u4F5CAOF\u7A33\u5065\uFF0C\u53EF\u4EE5\u5904\u7406\u8BEF\u64CD\u4F5C\u3002</p></li></ul><h3 id="_13-1-9-\u52A3\u52BF" tabindex="-1"><a class="header-anchor" href="#_13-1-9-\u52A3\u52BF" aria-hidden="true">#</a> 13.1.9 \u52A3\u52BF</h3><ul><li><p>\u6BD4\u8D77RDB\u5360\u7528\u66F4\u591A\u7684\u78C1\u76D8\u7A7A\u95F4\u3002</p></li><li><p>\u6062\u590D\u5907\u4EFD\u901F\u5EA6\u8981\u6162\u3002</p></li><li><p>\u6BCF\u6B21\u8BFB\u5199\u90FD\u540C\u6B65\u7684\u8BDD\uFF0C\u6709\u4E00\u5B9A\u7684\u6027\u80FD\u538B\u529B\u3002</p></li><li><p>\u5B58\u5728\u4E2A\u522BBug\uFF0C\u9020\u6210\u6062\u590D\u4E0D\u80FD\u3002</p></li></ul><h3 id="_13-1-10-\u5C0F\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#_13-1-10-\u5C0F\u603B\u7ED3" aria-hidden="true">#</a> 13.1.10 \u5C0F\u603B\u7ED3</h3><p><img src="https://img-1305804786.cos.ap-beijing.myqcloud.com//picgo202210221449991.png" alt="img" loading="lazy"></p><h2 id="_13-2-\u603B\u7ED3-which-one" tabindex="-1"><a class="header-anchor" href="#_13-2-\u603B\u7ED3-which-one" aria-hidden="true">#</a> 13.2 \u603B\u7ED3(Which one)</h2><h3 id="_13-2-1-\u7528\u54EA\u4E2A\u597D" tabindex="-1"><a class="header-anchor" href="#_13-2-1-\u7528\u54EA\u4E2A\u597D" aria-hidden="true">#</a> 13.2.1 \u7528\u54EA\u4E2A\u597D</h3><p>\u5B98\u65B9\u63A8\u8350\u4E24\u4E2A\u90FD\u542F\u7528\u3002</p><p>\u5982\u679C\u5BF9\u6570\u636E\u4E0D\u654F\u611F\uFF0C\u53EF\u4EE5\u9009\u5355\u72EC\u7528RDB\u3002</p><p>\u4E0D\u5EFA\u8BAE\u5355\u72EC\u7528 AOF\uFF0C\u56E0\u4E3A\u53EF\u80FD\u4F1A\u51FA\u73B0Bug\u3002</p><p>\u5982\u679C\u53EA\u662F\u505A\u7EAF\u5185\u5B58\u7F13\u5B58\uFF0C\u53EF\u4EE5\u90FD\u4E0D\u7528\u3002</p><h3 id="_13-2-2-\u5B98\u7F51\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#_13-2-2-\u5B98\u7F51\u5EFA\u8BAE" aria-hidden="true">#</a> 13.2.2 \u5B98\u7F51\u5EFA\u8BAE</h3><p><img src="https://img-1305804786.cos.ap-beijing.myqcloud.com//picgo202210221449173.png" alt="img" loading="lazy"></p><ul><li><p>RDB\u6301\u4E45\u5316\u65B9\u5F0F\u80FD\u591F\u5728\u6307\u5B9A\u7684\u65F6\u95F4\u95F4\u9694\u80FD\u5BF9\u4F60\u7684\u6570\u636E\u8FDB\u884C\u5FEB\u7167\u5B58\u50A8</p></li><li><p>AOF\u6301\u4E45\u5316\u65B9\u5F0F\u8BB0\u5F55\u6BCF\u6B21\u5BF9\u670D\u52A1\u5668\u5199\u7684\u64CD\u4F5C,\u5F53\u670D\u52A1\u5668\u91CD\u542F\u7684\u65F6\u5019\u4F1A\u91CD\u65B0\u6267\u884C\u8FD9\u4E9B\u547D\u4EE4\u6765\u6062\u590D\u539F\u59CB\u7684\u6570\u636E,AOF\u547D\u4EE4\u4EE5redis\u534F\u8BAE\u8FFD\u52A0\u4FDD\u5B58\u6BCF\u6B21\u5199\u7684\u64CD\u4F5C\u5230\u6587\u4EF6\u672B\u5C3E.</p></li><li><p>Redis\u8FD8\u80FD\u5BF9AOF\u6587\u4EF6\u8FDB\u884C\u540E\u53F0\u91CD\u5199,\u4F7F\u5F97AOF\u6587\u4EF6\u7684\u4F53\u79EF\u4E0D\u81F3\u4E8E\u8FC7\u5927</p></li><li><p>\u53EA\u505A\u7F13\u5B58\uFF1A\u5982\u679C\u4F60\u53EA\u5E0C\u671B\u4F60\u7684\u6570\u636E\u5728\u670D\u52A1\u5668\u8FD0\u884C\u7684\u65F6\u5019\u5B58\u5728,\u4F60\u4E5F\u53EF\u4EE5\u4E0D\u4F7F\u7528\u4EFB\u4F55\u6301\u4E45\u5316\u65B9\u5F0F.</p></li><li><p>\u540C\u65F6\u5F00\u542F\u4E24\u79CD\u6301\u4E45\u5316\u65B9\u5F0F</p></li><li><p>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B,\u5F53redis\u91CD\u542F\u7684\u65F6\u5019\u4F1A\u4F18\u5148\u8F7D\u5165AOF\u6587\u4EF6\u6765\u6062\u590D\u539F\u59CB\u7684\u6570\u636E, \u56E0\u4E3A\u5728\u901A\u5E38\u60C5\u51B5\u4E0BAOF\u6587\u4EF6\u4FDD\u5B58\u7684\u6570\u636E\u96C6\u8981\u6BD4RDB\u6587\u4EF6\u4FDD\u5B58\u7684\u6570\u636E\u96C6\u8981\u5B8C\u6574.</p></li><li><p>RDB\u7684\u6570\u636E\u4E0D\u5B9E\u65F6\uFF0C\u540C\u65F6\u4F7F\u7528\u4E24\u8005\u65F6\u670D\u52A1\u5668\u91CD\u542F\u4E5F\u53EA\u4F1A\u627EAOF\u6587\u4EF6\u3002\u90A3\u8981\u4E0D\u8981\u53EA\u4F7F\u7528AOF\u5462\uFF1F</p></li><li><p>\u5EFA\u8BAE\u4E0D\u8981\uFF0C\u56E0\u4E3ARDB\u66F4\u9002\u5408\u7528\u4E8E\u5907\u4EFD\u6570\u636E\u5E93(AOF\u5728\u4E0D\u65AD\u53D8\u5316\u4E0D\u597D\u5907\u4EFD)\uFF0C \u5FEB\u901F\u91CD\u542F\uFF0C\u800C\u4E14\u4E0D\u4F1A\u6709AOF\u53EF\u80FD\u6F5C\u5728\u7684bug\uFF0C\u7559\u7740\u4F5C\u4E3A\u4E00\u4E2A\u4E07\u4E00\u7684\u624B\u6BB5\u3002</p></li><li><p>\u6027\u80FD\u5EFA\u8BAE</p></li></ul><p>\u56E0\u4E3ARDB\u6587\u4EF6\u53EA\u7528\u4F5C\u540E\u5907\u7528\u9014\uFF0C\u5EFA\u8BAE\u53EA\u5728Slave\u4E0A\u6301\u4E45\u5316RDB\u6587\u4EF6\uFF0C\u800C\u4E14\u53EA\u898115\u5206\u949F\u5907\u4EFD\u4E00\u6B21\u5C31\u591F\u4E86\uFF0C\u53EA\u4FDD\u7559save 900 1\u8FD9\u6761\u89C4\u5219\u3002 \u5982\u679C\u4F7F\u7528AOF\uFF0C\u597D\u5904\u662F\u5728\u6700\u6076\u52A3\u60C5\u51B5\u4E0B\u4E5F\u53EA\u4F1A\u4E22\u5931\u4E0D\u8D85\u8FC7\u4E24\u79D2\u6570\u636E\uFF0C\u542F\u52A8\u811A\u672C\u8F83\u7B80\u5355\u53EAload\u81EA\u5DF1\u7684AOF\u6587\u4EF6\u5C31\u53EF\u4EE5\u4E86\u3002\u4EE3\u4EF7,\u4E00\u662F\u5E26\u6765\u4E86\u6301\u7EED\u7684IO\uFF0C\u4E8C\u662FAOF rewrite\u7684\u6700\u540E\u5C06rewrite\u8FC7\u7A0B\u4E2D\u4EA7\u751F\u7684\u65B0\u6570\u636E\u5199\u5230\u65B0\u6587\u4EF6\u9020\u6210\u7684\u963B\u585E\u51E0\u4E4E\u662F\u4E0D\u53EF\u907F\u514D\u7684\u3002\u53EA\u8981\u786C\u76D8\u8BB8\u53EF\uFF0C\u5E94\u8BE5\u5C3D\u91CF\u51CF\u5C11AOF rewrite\u7684\u9891\u7387\uFF0CAOF\u91CD\u5199\u7684\u57FA\u7840\u5927\u5C0F\u9ED8\u8BA4\u503C64M\u592A\u5C0F\u4E86\uFF0C\u53EF\u4EE5\u8BBE\u52305G\u4EE5\u4E0A\u3002\u9ED8\u8BA4\u8D85\u8FC7\u539F\u5927\u5C0F100%\u5927\u5C0F\u65F6\u91CD\u5199\u53EF\u4EE5\u6539\u5230\u9002\u5F53\u7684\u6570\u503C\u3002</p>',64),l=[d];function n(o,s){return i(),a("div",null,l)}const t=e(r,[["render",n],["__file","13.Redis\u6301\u4E45\u5316\u4E4BAOF.html.vue"]]);export{t as default};
