        <span class="token class-name">String</span> v <span class="token operator">=</span> map<span class="token punctuation">.</span><span class="token function">put</span><span class="token punctuation">(</span><span class="token string">"name"</span><span class="token punctuation">,</span> <span class="token string">"三更草堂"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
